//基于res.end的静态服务文件
const http = require('http'),
      path = require('path'),
      fs = require('fs');

http.createServer((req,res)=>{
  // console.log(req);
  // console.log(req.headers); 显示头
  // console.log(req.url); 显示请求的url地址
  var file = path.join(__dirname,req.url);
  try{
      res.end(fs.readFileSync(file).toString('utf-8'));
  }catch(err){
      res.end(err.message);
  }
}).listen(8080);
//基于流的静态服务文件
const http = require('http'),
      path = require('path'),
      fs = require('fs');

http.createServer((req,res)=>{
  var file = path.join(__dirname,req.url);
