HomeMsg.propTypes = {
  value: PropTypes.number.isRequired,   //要求数字类型，没有提供会警告
  onIncreaseClick: PropTypes.func.isRequired //要求函数类型
}

//  将state映射到Counter组件的props
function mapStateToProps(state) {
    return {
      value: state.Count
    }
  }

