      transform: translateZ(50px);
    }

    .animate_box:hover li:nth-child(6) {
      transform: translateZ(100px);
      background: #be46d8;
      border: 0;
      border-radius: 50%;
    }

    .animate_box ul:hover {
      transform: rotateX(9000deg) rotateY(5000deg);
      transition: all 100s linear;
    }

